import React from 'react'
import axios from 'axios'
import Box from '@mui/material/Box';
import { useState } from 'react';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import Card from '@mui/material/Card';
import CardMedia from '@mui/material/CardMedia';
import NavigateNextIcon from '@mui/icons-material/NavigateNext';
import Typography from '@mui/material/Typography';
import {Toaster, toast} from 'react-hot-toast'
import { Fab, Input } from '@mui/material';
import './FaceSwapper.css'

export default function FaceFilter() {

  const imgSrc=[
    "//cdn.shopify.com/s/files/1/1772/6223/products/download.png?v=1571610666", 
    "https://smoothitalia.com/wp-content/uploads/2021/06/stussy-jute-weave-bucket-hat-natural-1321047-01.png", 
    "https://images.squarespace-cdn.com/content/v1/580cf7ce6b8f5b3576a3da9f/1499667187052-8SHBB2EJGSQ2F35U8MF9/Screen%2BShot%2B2017-06-21%2Bat%2B1.19.41%2BAM.png" ,
    "https://images.vestiairecollective.com/cdn-cgi/image/w=500,h=500,q=80,f=auto,https://images.vestiairecollective.com/cdn-cgi/image//produit/black-wool-zara-hat-10874735-1_1.jpg" ,
    "https://img-static.tradesy.com/item/29888948/zara-gray-tie-dye-cup-size-s-m-hat-0-1-300-300.jpg"
  ]

  const handleNext = ()=>{
    setIndex((index + 1) % imgSrc.length)
    // fetch(imgSrc[index],  {
    //   mode: 'no-cors',
    //   credentials: 'include',
    //   method: 'GET',
    //   // headers: headers
    // })
    // .then(function(response) {
    //   return response.blob()
    // })
    // .then(function(blob) {
    //   setSelectedSrcImage(blob)
    // });
    let headers = new Headers();

  headers.append('Content-Type', 'application/json');
  headers.append('Accept', 'application/json');

  headers.append('Access-Control-Allow-Origin', 'http://localhost:3000');
  headers.append('Access-Control-Allow-Credentials', 'true');

  headers.append('GET', 'POST', 'OPTIONS');

  headers.append('Authorization', 'Basic ' + base64.encode(username + ":" + password));

    fetch(imgSrc[index], {
      method: "GET",
      headers: headers

      })
      .then(res => res.blob())
      .then(image => {
              setSelectedSrcImage(image)
              console.log('image=', image);
          }) 
      .catch(error => {
          console.error(error);
        }); 
    
  }



  const [index, setIndex] = useState(0)
  const [selectedSrcImage, setSelectedSrcImage] = useState(null);  
  const [selectedDestImage, setSelectedDestImage] = useState(null); 
  const [resultImage, setResultImage] = useState(null);  
    

  const handleResult = () => {
  const url = 'http://127.0.0.1:5000/img'
  const formData = new FormData()
  formData.append('srcFile', selectedSrcImage)
  formData.append('destFile', selectedDestImage)
  fetch(url, {
  method: "POST",
  body: formData
  })
  .then(res => res.blob())
  .then(image => {
          setResultImage(image)
          console.log('image=', image);
      }) 
  .catch(error => {
      console.error(error);
    }); 
}
  return (
    <>
    <Toaster 
      position="top-right"
      reverseOrder={true}/>
    <Button onClick={handleResult}>ena te3 TEST JEMLA</Button>
    <Box className='filter-image-container'>
      <div className='upload-container'>
            <label>Paste image here...</label>
            <br/>
            <label id="ext">Only JPG and PNG.</label>
            {selectedSrcImage && (
            <div>
              <img alt="not fount" width={"250px"} src={URL.createObjectURL(selectedSrcImage)} />
            </div>
            )}
            <Stack className='upload_button'>
              <label htmlFor="contained-button-file">
                <Input accept="image/*" id="contained-button-file" multiple type="file" 
                      name="myImage"
                      onChange={(event) => {
                        console.log(event.target.files[0]);
                        setSelectedSrcImage(event.target.files[0]);
              }}  />
                {/* <Button variant="contained"  component="span" style={{backgroundColor: "#EEB902"}}>
                  Upload
                </Button> */}
              </label>
            </Stack>
      </div>  
      <div className='upload-container'>
        <Box style={{'display': 'flex' , 'justifyItems': 'center' , 'marginBottom': '17%'}}>
          <Card className="Card"
                      sx={{ width :"50%", display: 'flex', flexDirection: 'column' , justifyItems: 'center'}}>
              <CardMedia
                  component="img"
                  image={imgSrc[index]}
                  alt="random"
                />
            </Card>
              <Fab size="small" color="secondary" aria-label="add" 
                    style={{'alignSelf' : 'center', 'marginLeft': '15%'}}
              onClick={handleNext} >
              <NavigateNextIcon />
            </Fab>
        </Box>
          <label>Paste image here...</label>
          <br/>
          <label id="ext">Only JPG and PNG.</label>
          {selectedDestImage && (
          <div>
            <img alt="not fount" width={"250px"} src={URL.createObjectURL(selectedDestImage)} />
          </div>
          )}
          <Stack className='upload_button'>
            <label htmlFor="contained-button-file">
              <Input  accept="image/*" id="contained-button-file" multiple type="file" 
                    name="myImage"
                    onChange={(event) => {
                      console.log(event.target.files[0]);
                      setSelectedDestImage(event.target.files[0]);
            }}  />
              {/* <Button variant="contained"  component="span" style={{backgroundColor: "#EEB902"}}>
                Upload
              </Button> */}
            </label>
          </Stack>
      </div>
      <div className='upload-container'>
      <label>Result ...</label>
          <br/>
          <label id="ext">isn't that amazing!</label>
          {resultImage && (
          <div>
            <img alt="not fount" width={"250px"} src={URL.createObjectURL(resultImage)} />
          </div>
          )}
      </div>
    </Box>
  </>
  )
}
