import React from 'react'
import Box from '@mui/material/Box';
import FileCopyIcon from '@mui/icons-material/FileCopyOutlined';
import SaveIcon from '@mui/icons-material/Save';
import PrintIcon from '@mui/icons-material/Print';
import ShareIcon from '@mui/icons-material/Share';
import { useState } from 'react';
import { styled } from '@mui/material/styles';
import Stack from '@mui/material/Stack';
import Stepper from '@mui/material/Stepper';
import Step from '@mui/material/Step';
import StepLabel from '@mui/material/StepLabel';
import StepContent from '@mui/material/StepContent';
import Button from '@mui/material/Button';
import Paper from '@mui/material/Paper';
import Card from '@mui/material/Card';
import CardMedia from '@mui/material/CardMedia';
import CardContent from '@mui/material/CardContent';
import CardActions from '@mui/material/CardActions';
import IconButton, { IconButtonProps } from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import FavoriteIcon from '@mui/icons-material/Favorite';
import {Toaster, toast} from 'react-hot-toast'
import './FaceSwapper.css'


const Input = styled('input')({
  display: 'none',
});

const actions = [
  { icon: <FileCopyIcon />, name: 'Copy' },
  { icon: <SaveIcon />, name: 'Save' },
  { icon: <PrintIcon />, name: 'Print' },
  { icon: <ShareIcon />, name: 'Share' },
];

const handleChange = (event) => {
  const data = tableData
  data.fx= event.target.value
  axios
    .post('http://127.0.0.1:5000/send', data)
    .then((res)=>{

        setResult(res.data)
        const a=JSON.parse(res.data.cost_history)
        const ah=JSON.parse(res.data.cost_h_history)
       
            setSeries([{
              name: "Cost",
              data: a
          }])
            setSeriesh([{
              name: "Cost",
              data: ah
          }])

          setOptions(
            {
            chart: {
              height: 350,
              type: 'line',
              zoom: {
                enabled: false
              }
            },
            dataLabels: {
              enabled: false
            },
            stroke: {
              curve: 'straight'
            },
            title: {
              text: 'Cost History without inertia decay',
              align: 'left'
            },
            grid: {
              row: {
                colors: ['#f3f3f3', 'transparent'], // takes an array which will be repeated on columns
                opacity: 0.5
              },
            },
            xaxis: {
              categories: Array.from(Array(a.length).keys()),
            },
            yaxis:{
              decimalsInFloat: 2
            }
          },
        )
          setOptionsh(
            {
            chart: {
              height: 350,
              type: 'line',
              zoom: {
                enabled: false
              }
            },
            dataLabels: {
              enabled: false
            },
            stroke: {
              curve: 'straight'
            },
            title: {
              text: 'Cost History with inertia decay',
              align: 'left'
            },
            grid: {
              row: {
                colors: ['#f3f3f3', 'transparent'], // takes an array which will be repeated on columns
                opacity: 0.5
              },
            },
            xaxis: {
              categories: Array.from(Array(a.length).keys()),
            },
            yaxis:{
              decimalsInFloat: 2
            }
          },
        )

        setSeries1([{
          name: "position",
          data: JSON.parse( res.data.pos_history)
        }])

        setOptions1({
          chart: {
            height: 350,
            type: 'scatter',
            zoom: {
              enabled: true,
              type: 'xy'
            }
          },
          xaxis: { 
            floating: true,
          },
          yaxis: { 
            decimalsInFloat: 2
          },
          title: {
            text: 'Animating swarms without inertia decay',
            align: 'left'
          },
        })

        setSeries1h([{
          name: "position",
          data: JSON.parse( res.data.pos_h_history)
        }])

        setOptions1h({
          chart: {
            height: 350,
            type: 'scatter',
            zoom: {
              enabled: true,
              type: 'xy'
            }
          },
          xaxis: { 
            floating: true,
          },
          yaxis: { 
            decimalsInFloat: 2
          },
          title: {
            text: 'Animating swarms with inertia decay ',
            align: 'left'
          },
        })    
    setfn(event.target.value)
    })
    .catch((err)=>{
        console.log(err)
    });
}
export default function FaceSwapper() {
  const [selectedSrcImage, setSelectedSrcImage] = useState(null);  
  const [selectedDestImage, setSelectedDestImage] = useState(null);  
const [activeStep, setActiveStep] = React.useState(0);

const steps = [
  {
    label: 'Source image',
    description: 
    <>
      <div className='upload-container'>
        <label>Paste image here...</label>
        <br/>
        <label id="ext">Only JPG and PNG.</label>
        {selectedSrcImage && (
        <div>
          <img alt="not fount" width={"250px"} src={URL.createObjectURL(selectedSrcImage)} />
        </div>
        )}
        <Stack className='upload_button'>
          <label htmlFor="contained-button-file">
            <Input accept="image/*" id="contained-button-file" multiple type="file" 
                  name="myImage"
                  onChange={(event) => {
                    console.log(event.target.files[0]);
                    setSelectedSrcImage(event.target.files[0]);
          }}  />
            <Button variant="contained"  component="span" style={{backgroundColor: "#EEB902"}}>
              Upload
            </Button>
          </label>
        </Stack>
    </div>
    </>
    ,
  },
  {
    label: 'Destination image',
    description:
    <>
    <div className='upload-container'>
      <label>Paste image here...</label>
      <br/>
      <label id="ext">Only JPG and PNG.</label>
      {selectedDestImage && (
      <div>
        <img alt="not fount" width={"250px"} src={URL.createObjectURL(selectedDestImage)} />
      </div>
      )}
      <Stack className='upload_button'>
        <label htmlFor="contained-button-file">
          <Input accept="image/*" id="contained-button-file" multiple type="file" 
                name="myImage"
                onChange={(event) => {
                  console.log(event.target.files[0]);
                  setSelectedDestImage(event.target.files[0]);
        }}  />
          <Button variant="contained"  component="span" style={{backgroundColor: "#EEB902"}}>
            Upload
          </Button>
        </label>
      </Stack>
  </div>
  </>
    ,
  },
  {
    label: 'Result',
    description: 
    <>
    <Card sx={{ maxWidth: 345 }}>
      <CardMedia
        component="img"
        height="200"
        image="../src.jpg"
        alt="Result"
      />
      <CardContent>
      </CardContent>
      <CardActions disableSpacing>
        <IconButton aria-label="add to favorites">
          <FavoriteIcon />
        </IconButton>
        <IconButton aria-label="share">
          <ShareIcon />
        </IconButton>
      </CardActions>
    </Card>
    </>
  },
];

const handleNext = () => {
  setActiveStep((prevActiveStep) => prevActiveStep + 1);
};

const handleAlert=(step)=>{
  console.log('step', step);
  if(step==0)
    if(selectedSrcImage===null)
      toast.error('upload source image first!')
    else
        handleNext();
  if(step==1)
      if(selectedDestImage===null)
        toast.error('upload destination image first!')
      else
        handleNext();
  if(step==2){
    toast('Good Job!', {
      icon: 'ðŸ‘',
    });
    handleNext() ;}
}

const handleBack = () => {
  setActiveStep((prevActiveStep) => prevActiveStep - 1);
};

const handleReset = () => {
  setActiveStep(0);
};


  return (
    <>
    <Toaster 
      position="top-right"
      reverseOrder={true}/>
    <Box className='stepper-container'>
      <Stepper activeStep={activeStep} orientation="vertical">
        {steps.map((step, index) => (
          <Step key={step.label} sx={{
            '& .MuiStepLabel-root .Mui-completed': {
              color: '#97CC04', // circle color (COMPLETED)
            },
            '& .MuiStepLabel-root .Mui-active': {
              color: '#97CC04', // circle color (ACTIVE)
            },
            '& .MuiStepLabel-label .Mui-active .MuiStepLabel-alternativeLabel':
              {
                color: 'black', // Just text label (ACTIVE)
              },
            '& .MuiStepLabel-root .Mui-active .MuiStepIcon-text': {
              fill: 'white', // circle's number (ACTIVE)
            },
          }}>
            <StepLabel 
              optional={
                index === 2 ? (
                  <Typography variant="caption">Last step</Typography>
                ) : null
              }
            >
              {step.label}
            </StepLabel>
            <StepContent>
              <div>{step.description}</div>
              <Box sx={{ mb: 2 }}>
                <div>
                  <Button style={{backgroundColor: "#97CC04"}}
                    variant="contained"
                    onClick={()=>{handleAlert(index);}}
                    sx={{ mt: 1, mr: 1 }}
                  >
                    {index === steps.length - 1 ? 'Finish' : 'Continue'}
                  </Button>
                  <Button
                    disabled={index === 0}
                    onClick={handleBack}
                    sx={{ mt: 1, mr: 1 }}
                  >
                    Back
                  </Button>
                </div>
              </Box>
            </StepContent>
          </Step>
        ))}
      </Stepper>
      {activeStep === steps.length && (
        <Paper square elevation={0} sx={{ p: 3 }}>
          <Typography>All steps completed - you&apos;re finished</Typography>
          <Button onClick={handleReset} sx={{ mt: 1, mr: 1 }}>
            Reset
          </Button>
        </Paper>
      )}
    </Box>
  </>
  )
}
