  //     console.error(error);
  //   });
  axios.get('http://127.0.0.1:5000/img').then((res)=>{
    
    setResultImage(image)
    console.log('1', typeof(image));
  })
  
}
const b64toBlob = (b64Data, contentType='', sliceSize=512) => {
  const byteCharacters = atob(b64Data);
  const byteArrays = [];

  for (let offset = 0; offset < byteCharacters.length; offset += sliceSize) {
    const slice = byteCharacters.slice(offset, offset + sliceSize);

    const byteNumbers = new Array(slice.length);
    for (let i = 0; i < slice.length; i++) {
      byteNumbers[i] = slice.charCodeAt(i);
    }

    const byteArray = new Uint8Array(byteNumbers);
    byteArrays.push(byteArray);
  }

  const blob = new Blob(byteArrays, {type: contentType});
  return blob;
}

  return (
    <>
    <Toaster 
      position="top-right"
      reverseOrder={true}/>
    <Button onClick={handleTest}>ena te3 TEST JEMLA</Button>
    <Box className='filter-image-container'>
      <div className='upload-container'>
            <label>Paste image here...</label>
            <br/>
            <label id="ext">Only JPG and PNG.</label>
            {selectedSrcImage && (
            <div>
              <img alt="not fount" width={"250px"} src={URL.createObjectURL(selectedSrcImage)} />
            </div>
            )}
            <Stack className='upload_button'>
              <label htmlFor="contained-button-file">
                <Input accept="image/*" id="contained-button-file" multiple type="file" 
                      name="myImage"
                      onChange={(event) => {
                        console.log(event.target.files[0]);
                        setSelectedSrcImage(event.target.files[0]);
              }}  />
                {/* <Button variant="contained"  component="span" style={{backgroundColor: "#EEB902"}}>
                  Upload
                </Button> */}
              </label>
            </Stack>
      </div>  
      <div className='upload-container'>
          <label>Paste image here...</label>
          <br/>
          <label id="ext">Only JPG and PNG.</label>
          {selectedDestImage && (
          <div>
            <img alt="not fount" width={"250px"} src={URL.createObjectURL(selectedDestImage)} />
          </div>
          )}
          <Stack className='upload_button'>
            <label htmlFor="contained-button-file">
              <Input  accept="image/*" id="contained-button-file" multiple type="file" 
                    name="myImage"
                    onChange={(event) => {
                      console.log(event.target.files[0]);
                      setSelectedDestImage(event.target.files[0]);
            }}  />
              {/* <Button variant="contained"  component="span" style={{backgroundColor: "#EEB902"}}>
                Upload
              </Button> */}
            </label>
          </Stack>
      </div>
      <div className='upload-container'>
      <label>Result ...</label>
          <br/>
          <label id="ext">isn't that amazing!</label>
          {resultImage && (
          <div>
            <img alt="not fount" width={"250px"} src={URL.createObjectURL(resultImage)} />
          </div>
          )}
      </div>
    </Box>
  </>
  )
}
